export const PostsExcerpts = gql`
    query PostsExcerpts($postsPerPage: Int!, $skip: Int!, $category: String!) {
  graphcms {
    posts(
      first: $postsPerPage
      stage: PUBLISHED
      skip: $skip
      orderBy: date_DESC
      where: {categories_some: {slug_starts_with: $category}}
    ) {
      author {
        ...PostAuthor
      }
      excerpt
      slug
      title
      coverImage {
        ...CoverImage
      }
    }
    postsConnection(
      stage: PUBLISHED
      where: {categories_some: {slug_starts_with: $category}}
    ) {
      aggregate {
        count
      }
    }
  }
}
    ${PostAuthor}
${CoverImage}`;
